import {useEffect, createContext, useState } from "react";

const authContext = createContext();
const petFinderKey = "WXh8Fm1P6GydlDOm9kP564MYwUs6ITqhBWye7MEwqcwvyU9XQy";
const petFinderSecret = "VSBGhUAftbzFDnPwh2pRc3x41qEVKGbMM96U74Uu";
function MyToken({ Component, pageProps }) {
  const [accessToken, setAccessToken] = useState(null);

useEffect(() => {
  const fetchAccessToken = async () => {
    const params = new URLSearchParams();
    params.append("grant_type", "client_credentials");
    params.append("client_id", petFinderKey);
    params.append("client_secret", petFinderSecret);
    const petFinderRes = await fetch("https://api.petfinder.com/v2/oauth2/token",
    {
      method: "POST",
      body: params
    }
    );
    console.log(await petFinderRes.json());
  };
  fetchAccessToken();
}, []);

return (
  <authContext.Provider>
    <Component {...pageProps} />
  </authContext.Provider>
);
}

export default MyToken;
